import { IDBPDatabase } from "idb";
import { Observable, Subject } from "rxjs";
import { mergeMap, map } from "rxjs/operators";

import DASHFeature from "../../../../../transports/dash";
import SMOOTHFeature from "../../../../../transports/smooth";

import {
  ILocalAdaptation,
  ILocalIndexSegment,
  ILocalManifest,
  ILocalPeriod,
  ILocalRepresentation,
} from "../../../../../parsers/manifest/local/types";
import { ILocalManifestOnline } from "../transports/types";
import { ISegmentInitStored, ISegmentStored } from "./types";
import { createManifestPipeline } from "../../../../../core/pipelines";
import Manifest from "../../../../../manifest";
import { ITransportPipelines } from "../../../../../transports";

/**
 * Launch the pipeline by url
 *
 * @param manifestURL - Manifest url on the web
 * @param transport - Transport that need to be use.
 * @returns The rxpManifest ready to be inserted in IndexDB
 *
 */
export function manifestLoader(
  manifestURL: string,
  transport: "smooth" | "dash" = "dash",
): Observable<{ manifest: Manifest; transportPipelines: ITransportPipelines }> {
  const pipelineTypes = {
    smooth: SMOOTHFeature,
    dash: DASHFeature,
  };
  const transportPipelines = pipelineTypes[transport]({
    lowLatencyMode: false,
  });
  const manifestPipeline = createManifestPipeline(
    transportPipelines,
    {
      lowLatencyMode: false,
      manifestRetry: 5,
      offlineRetry: 5,
    },
    new Subject(),
  );
  return manifestPipeline
    .fetch(manifestURL)
    .pipe(
      mergeMap(response =>
        manifestPipeline
          .parse(response.value, manifestURL)
          .pipe(map(({ manifest }) => ({ manifest, transportPipelines }))),
      ),
    );
}

/**
 * Returns the structure of the manifest needed by the rxPlayer transport local.
 *
 * @remarks
 * It's mandatory to construct again the rxpManifest
 * when the user want it because we can't insert function type in IndexDB
 *
 * @param ILocalManifestOnline - The rxpManifest we downloaded when online
 * @returns The manifest that the rxPlayer expect
 *
 */
export function offlineManifestLoader(
  rxpManifestOnlineFormat: ILocalManifestOnline,
  db: IDBPDatabase,
): ILocalManifest {
  return {
    ...rxpManifestOnlineFormat,
    type: "local",
    periods: rxpManifestOnlineFormat.periods.map(
      (period): ILocalPeriod => ({
        ...period,
        adaptations: period.adaptations.map(
          (adaptation): ILocalAdaptation => ({
            ...adaptation,
            type: adaptation.type as "audio" | "video" | "text",
            representations: adaptation.representations.map(
              (representation): ILocalRepresentation => {
                return {
                  ...representation,
                  index: {
                    loadInitSegment({ resolve, reject }) {
                      if (representation.index.init) {
                        db.get("segments", representation.index.init)
                          .then(
                            (segmentIndex: ISegmentInitStored | undefined) => {
                              if (!segmentIndex) {
                                reject(new Error("Segment not found"));
                                return;
                              }
                              resolve({
                                data: segmentIndex.data,
                              });
                              return;
                            },
                          )
                          .catch(err => {
                            reject(err);
                          });
                        return;
                      }
                      resolve({
                        data: null,
                      });
                      return;
                    },
                    loadSegment(segmentID, { resolve, reject }) {
                      db.get("segments", segmentID)
                        .then((segment: ISegmentStored | undefined) => {
                          if (!segment) {
                            reject(new Error("Segment not found"));
                            return;
                          }
                          resolve({
                            data: segment.data,
                          });
                          return;
                        })
                        .catch((err: Error | undefined) => {
                          reject(err);
                        });
                    },
                    segments: representation.index.segments.map(
                      (segment): ILocalIndexSegment => {
                        if (!Array.isArray(segment)) {
                          return {
                            duration: 0,
                            time: 0,
                            timescale: 0,
                          };
                        }
                        const [, time, timescale, duration] = segment;
                        return {
                          duration,
                          time,
                          timescale,
                        };
                      },
                    ),
                  },
                };
              },
            ),
          }),
        ),
      }),
    ),
  };
}
